stages.script:
var main_q = request.getFirstParam('q');
if (main_q != '*:*' && main_q != '*') { request.putSingleParam('main_q',main_q) };

var filters = request.getParam('fq');
if (filters.size != 0) { 
  request.putSingleParam("filters", java.lang.String.join(',',filters));
}

var headers = request.headers;
request.addParam('username', headers.getFirst('fusion-user-name'));
request.addParam('roles', headers.get("fusion-role-names"));
request.addParam("headers", headers.toString());
request.putSingleParamIfAbsent('now', (new Date()).toISOString());
request.putSingleParamIfAbsent('wt', 'json');

//var ST = Java.type('org.stringtemplate.v4.ST');
//var template = new ST('context: <context> :: request: <request.firstParam("main_q")>');
//template.add('context',context);
//template.add('request',request);
//request.putSingleParam("ST",template.render());

if (!ctx.hasProperty('applicable_rules')) { exit; }
// TODO: or if rules numFound == 0?
// TODO: set some context data if no rules data or zero rules?

var applicable_rules_data = ctx.getProperty('applicable_rules_data');

var rules_to_exclude = request.getParam('rules.exclude');

// Arrays of things to put into response (by way of the ctx/context and the post-Solr stage)
var rules_response = {
  "excluded_rules": [],
  "banners": [],
  "redirects": [],
  "applicable_rules": [],
  "applicable_rules_ids": [],
  "applicable_rules_debug": [],
  "rules_errors": []
};

if (applicable_rules_data.response.docs.size() != applicable_rules_data.response.numFound) {
   rules_response["rules_errors"].push(("Not all matched rules processed: size=" + applicable_rules_data.response.docs.size() + " numFound=" + applicable_rules_data.response.numFound).toString());
}

for(var rule_index=0; rule_index < applicable_rules_data.response.docs.size(); rule_index++) {
  var rule_doc = applicable_rules_data.response.docs[rule_index];
  rules_response["applicable_rules_ids"].push(rule_doc.id);
  rules_response["applicable_rules"].push(rule_doc);
  rules_response["applicable_rules_debug"].push(rule_doc.toString());
  var excluded = false;

  if (rules_to_exclude) {
    // TODO: optimize this check, add support for comma-separated values too
    for (var i=0; i < rules_to_exclude.size(); i++) {
      if (rule_doc.id == rules_to_exclude[i]) {
        excluded = true;
        rules_response["excluded_rules"].push(rules_to_exclude[i]);
        break;
      }
    }
  }

  if (!excluded) {
    var rule_type = rule_doc.type;

    // Add response values - every rule type can have multivalued parallel keys/values values
    if (rule_doc.keys) {
      for (var kx=0; kx < rule_doc.keys.size(); kx++) {
        if (!rules_response[rule_doc.keys[kx]]) { rules_response[rule_doc.keys[kx]] = []; }
        rules_response[rule_doc.keys[kx]].push(rule_doc.values[kx]);
      }
    }

    switch (rule_type) {
      case 'response_value':   // e.g. key="banner", "redirect"... etc
        // response values evolved to being available for all rule types, but if all you
        // want to do is set a banner this placeholder type suffices
        break;

      case 'set_params':
        for (var px=0; px < rule_doc.param_keys.size(); px++) {

          switch (rule_doc.param_policies[px]) {
            case 'append':
              request.addParam(rule_doc.param_keys[px], rule_doc.param_values[px]);
              break

            case 'replace':
              request.putSingleParam(rule_doc.param_keys[px], rule_doc.param_values[px]);
              break
          }
        }
        break;

      case 'query_rewrite':
        // effectively same as a set_params replace of q
        if (rule_doc.new_q) { request.putSingleParam("q", rule_doc.new_q[0]); }
        break;

      case 'boost':
        // boost_param=bq, boost_query=id:2^5
        // effectively same as a set_params append of boost or bq.
        request.addParam(rule_doc.boost_param, rule_doc.boost_query);
        break;

      case 'block_list':
        // effectively same as set_params append of an fq
        request.addParam("fq", "-{!terms f=" + rule_doc.field_name + "}" + Java.type('java.lang.String').join(',',rule_doc.field_values));
        break;

      case 'filter_list':
        // effectively same as set_params append of an fq, and a replace of q with `*:*`
        request.addParam("fq", "{!terms f=" + rule_doc.field_name + "}" + Java.type('java.lang.String').join(',',rule_doc.field_values));
        request.putSingleParam('q','*:*');
        break;

      case 'boost_list':
        // effectively same as set_params append of a boost
        // TODO: need to add code to make `id:1^=10 id:2^=9...`
        request.addParam("bq", "({!terms f=" + rule_doc.field_name + "}" + Java.type('java.lang.String').join(',',rule_doc.field_values) + ')^5');
        break;

      default:
        rules_response["rules_errors"].push(("Unknown rule type '" + rule_type + "' on rule " + rule_doc.id).toString());
        break;
    }
  }
}

ctx.setProperty("rules_response", rules_response);

function add_to_response(key, list) {
  if (list.length > 0) {
      response.initialEntity.appendStringList(key, Java.to(list, Java.type('java.util.List')));
  }
}

var rules_response = ctx.getProperty("rules_response")
if (rules_response) {
  for (var k in rules_response) {
    add_to_response(k, rules_response[k]);
  }

  // Duplicate redirect responses to supported landing-pages
  if (rules_response["redirect"]) {
    add_to_response('landing-pages', rules_response["redirect"]);
  }
}

// This only works with JSON response format (wt=json); TODO: maybe convert the data to JSON and emit as a string?
response.initialEntity.appendObject("applicable_rules_data", ctx.getProperty('applicable_rules_data'));
